SELECT (SELECT CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) FROM hr.employees AS e WHERE e.EMPLOYEE_ID = j_h.EMPLOYEE_ID) AS 'Nome completo', CONCAT(DAY(j_h.START_DATE),'/',MONTH(j_h.START_DATE),'/',YEAR(j_h.START_DATE)) AS 'Data de início do cargo', CONCAT(DAY(j_h.END_DATE),'/',MONTH(j_h.END_DATE),'/',YEAR(j_h.END_DATE)) AS 'Data de rescisão', ROUND(DATEDIFF(j_h.END_DATE, j_h.START_DATE ) / 365, 2) AS 'Anos trabalhados' FROM hr.job_history AS j_h WHERE EMPLOYEE_ID <> 0 ORDER BY `Nome completo` , `Anos trabalhados`;