SELECT (SELECT CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) FROM hr.employees AS e WHERE e.EMPLOYEE_ID = j_h.EMPLOYEE_ID) AS 'Nome completo', (SELECT j.JOB_TITLE FROM hr.jobs AS j WHERE j.JOB_ID = j_h.JOB_ID) AS 'Cargo', j_h.START_DATE AS 'Data de in√≠cio do cargo', (SELECT d.DEPARTMENT_NAME FROM hr.departments AS d WHERE d.DEPARTMENT_ID = j_h.DEPARTMENT_ID) AS 'Departamento' FROM hr.job_history AS j_h WHERE j_h.START_DATE <> '0000-00-00' ORDER BY (SELECT CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) FROM hr.employees AS e WHERE e.EMPLOYEE_ID = j_h.EMPLOYEE_ID) DESC, (SELECT j.JOB_TITLE FROM hr.jobs AS j WHERE j.JOB_ID = j_h.JOB_ID);