SELECT CONCAT(first_name,' ',last_name) AS `Nome completo`,J.job_title AS 'Cargo',JH.START_DATE AS `Data de início do cargo`,DP.DEPARTMENT_NAME AS 'Departamento' FROM hr.employees AS E INNER JOIN hr.job_history AS JH ON JH.employee_id= E.job_employee_id INNER JOIN hr.jobs AS J  ON JH.JOB_ID = J.JOB_ID INNER JOIN hr.departments AS DP ON DP.DEPARTMENT_ID = JH.DEPARTMENT_ID ORDER BY `Nome completo` DESC,Cargo ASC ;SELECT CONCAT(first_name,' ',last_name) AS 'Nome completo',J.job_title AS 'Cargo',JH.START_DATE AS `Data de início do cargo`,DP.DEPARTMENT_NAME AS 'Departamento' FROM hr.employees AS E INNER JOIN hr.job_history AS JH ON E.EMPLOYEE_ID = JH.EMPLOYEE_ID INNER JOIN hr.jobs AS J  ON JH.JOB_ID = J.JOB_ID INNER JOIN hr.departments AS DP ON DP.DEPARTMENT_ID = JH.DEPARTMENT_ID ORDER BY `Nome completo` DESC,Cargo ASC ;
