SELECT (SELECT UCASE(CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME)) FROM hr.employees AS e WHERE e.EMPLOYEE_ID = j_h.EMPLOYEE_ID) AS 'Nome completo', j_h.START_DATE AS 'Data de início', (SELECT e.SALARY FROM hr.employees AS e WHERE e.EMPLOYEE_ID = j_h.EMPLOYEE_ID) AS 'Salário' FROM hr.job_history AS j_h WHERE	j_h.START_DATE <> '0000-00-00' AND MONTH(j_h.START_DATE) IN ('01', '02', '03') ORDER BY (SELECT CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) FROM hr.employees AS e WHERE e.EMPLOYEE_ID = j_h.EMPLOYEE_ID), j_h.START_DATE;